        const {list} = this.state
        const result = -1

        list.forEach((listItem, index) => {
            if(listItem.key === key){
                result = index
            }
        })
        return result
    }
    
    onUpdate = key => {
        var {list} = this.state
        const index = this.findIndexInList(key)
