        var {list}= this.state
        // console.log(list)

        return (
            <Layout>
                <Form onChange={this.onChange}/>
                
                <ListTable data = {list} onDelete = {this.onDelete} onReset = {this.onReset} onUpdate = {this.onUpdate}/>
            </Layout>

        )
    }
}

export default Home
