import React from 'react'
import {
    Form,
    Select,
    Input,
    Typography,
    Radio,
    Slider,
    Button,
    Upload,
    Rate,
    Checkbox,
    Row,
    Col,
} from 'antd';

const { Option } = Select;
const {Title} = Typography
const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

class Home extends React.Component {

    onFinish = values => {
        console.log('Received values of form: ', values);
    }
    render() {
        return (
            <Form
                name="form"
                {...formItemLayout}
                onFinish={this.onFinish}
                initialValues={{
                    'input-number': 3,
                    'checkbox-group': ['A', 'B'],
                    rate: 3.5,
                }}
                style ={
                    {
                        background : 'white',
                        display : 'block',
                        width : '75%',
                        margin : '0 auto',
                        padding : '30px 0'
                    }
                }
            >
                <Title level = {2}>TO-do-app (Designed by Lỗ Tấn)</Title>
                <Form.Item label="Name" name="username">
                    <Input placeholder="Typing here..." />
                </Form.Item>
                <Form.Item
                    name="country"
                    label="Country"
                    hasFeedback
                    rules={[
                        {
                            required: true,
                            message: 'Please select your country!',
                        },
                    ]}
                >
                    <Select placeholder="Please select a country">
                        <Option value="china">China</Option>
                        <Option value="usa">U.S.A</Option>
                        <Option value="vn">Viet Nam</Option>
                    </Select>
                </Form.Item>

                <Form.Item
                    name="colors"
                    label="Select your favourite color"
                    rules={[
                        {
                            required: true,
                            message: 'Please select your favourite colors!',
                            type: 'array',
                        },
                    ]}
                >
                    <Select mode="multiple" placeholder="Please select favourite colors">
                        <Option value="red">Red</Option>
                        <Option value="green">Green</Option>
                        <Option value="blue">Blue</Option>
                    </Select>
                </Form.Item>





                <Form.Item name="gender" label="Gender">
                    <Radio.Group>
                        <Radio value="male">Male</Radio>
                        <Radio value="female">Female</Radio>

                    </Radio.Group>
                </Form.Item>



                <Form.Item name="Hobby" label="Checkbox.Group">
                    <Checkbox.Group>
                        <Row>
                            <Col span={8}>
                                <Checkbox
                                    value="soccer"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    Soccer
              </Checkbox>
                            </Col>
                            <Col span={12}>
                                <Checkbox
                                    value="swimming"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                    disabled
                                >
                                    Swimming
              </Checkbox>
                            </Col>
                            <Col span={8}>
                                <Checkbox
                                    value="dance"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    Dance
              </Checkbox>
                            </Col>


                        </Row>
                    </Checkbox.Group>
                </Form.Item>

                <Form.Item
                    wrapperCol={{
                        span: 12,
                        offset: 6,
                    }}
                >
                    <Button type="primary" htmlType="submit">
                        Submit
        </Button>
                </Form.Item>
            </Form>



        )
    }
}

export default Home
