import React from 'react'
import Form from './../components/Form'
import ListTable from '../components/ListTable'
import {Layout} from 'antd'


class Home extends React.Component {

    

    constructor(props){
        super(props)
        this.state = {
            list : []
        }
    }

    componentDidMount(){
        if(list.length === 0){
            
        }
        var data = JSON.parse(localStorage.getItem('LIST'));

    }
    onChange = objectForm => {
        var {list} =this.state
        list.push({
            ...objectForm,
            key : list.length
        })
        // console.log('h ia zai')
        // console.log('list', list)
        // var key = list.length
        // list = [...list, objectForm, key]
        this.setState({
            list : list
        })
        localStorage.setItem('LIST', JSON.stringify(this.state));   
     
    }
  

    render() {
        var {list}= this.state
        console.log(list)

        return (
            <Layout>
                <Form onChange={this.onChange}/>
                <ListTable data = {list}/>
            </Layout>

        )
    }
}

export default Home
