import React, { Component } from 'react'
import { Table, Tag, Space } from 'antd';

const columns = [
    {
        title: 'Name',
        dataIndex: 'name',
        key: 'name',
        render: text => <a>{text}</a>,
    },
    {
        title: 'Country',
        dataIndex: 'country',
        key: 'country',
    },
    {
        title: 'Gender',
        dataIndex: 'gender',
        key: 'gender',
        
    },
    {
        title: 'Color',
        key: 'colors',
        dataIndex: 'color',
        render: color => (
            <>
                {color.map(tag => {
                    let color = tag.length > 5? 'geekblue' : 'green';
                    if (tag === 'loser') {
                        color = 'volcano';
                    }
                    return (
                        <Tag color={color} key={tag}>
                            {tag.toUpperCase()}
                        </Tag>
                    );
                })}
            </>
        ),
    },
    {
        title: 'Action',
        key: 'action',
        render: (text, record) => (
            <Space size="middle">
                <a>Edit</a>
                <a>Delete</a>
            </Space>
        ),
    },
];

const data = [
    {
        key: '1',
        name: 'John Brown',
        age: 32,
        address: 'New York No. 1 Lake Park',
        tags: ['nice', 'developer'],
    }
]


class ListTable extends React.Component {
    render() {
        return (
            <Table columns={columns} dataSource={data} />
        )
    }
}

export default ListTable
