import React, { Component } from 'react'
import { Table, Tag, Space } from 'antd';


onDelete = key =>{
    this.props.onDelete(key)
}

const columns = [
    {
        title: 'Name',
        dataIndex: 'username',
        key: 'username',
        render: text => <a className = "table-name">{text}</a>,
    },
    {
        title: 'Country',
        dataIndex: 'country',
        key: 'country',
    },
    {
        title: 'Gender',
        dataIndex: 'gender',
        key: 'gender',
        
    },
    {
        title: 'Color',
        key: 'tags',
        dataIndex: 'color',
        // render: tags => (
        //     <>
        //         {tags.map(tag => {
        //             let color = tag.length > 5? 'geekblue' : 'green';
        //             if (tag === 'loser') {
        //                 color = 'volcano';
        //             }
        //             return (
        //                 <Tag color={color} key={tag}>
        //                     {tag.toUpperCase()}
        //                 </Tag>
        //             );
        //         })}
        //     </>
        // ),
    },
    {
        title: 'Action',
        key: 'action',
        render: (text, record) => (
            <Space size="middle">
                <a className = "btn-edit">Edit</a>
                <a className = "btn-delete" onClick = {this.props.onDelete(record.key)}>Delete</a>
            </Space>
        ),
    },
];


// const data = []


class ListTable extends React.Component {
    // state ={
    //     status : 0
    // }

    // componentWillReceiveProps(previousProps){
    //     console.log(this.props.data)
    //     console.log(previousProps.data)
    //     if(this.props.data !== previousProps.data){
    //         this.setState({
    //             status : 1
    //         })
    //     }

    // }

    render() {

       var {data} = this.props
    //    console.log(data)
        

        return (
            <Table key={Math.random()} columns={columns} dataSource={data} style={
                {
                    display : 'block',
                    width : '75%',
                    margin : '0 auto',
                    background : 'white',
                    
                }
            } />
        )
    }
}

export default ListTable
