import React from 'react'
import {
    Form,
    Select,
    Input,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Rate,
    Checkbox,
    Row,
    Col,
} from 'antd';

const { Option } = Select;
const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};








class Home extends React.Component {

    onFinish = values => {
        console.log('Received values of form: ', values);
    }
    render() {
        return (
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={this.onFinish}
                initialValues={{
                    'input-number': 3,
                    'checkbox-group': ['A', 'B'],
                    rate: 3.5,
                }}
            >
                <Form.Item label="Name" name="username">
                    <Input placeholder = "Typing here..." />
                </Form.Item>
                <Form.Item
                    name="country"
                    label="Country"
                    hasFeedback
                    rules={[
                        {
                            required: true,
                            message: 'Please select your country!',
                        },
                    ]}
                >
                    <Select placeholder="Please select a country">
                        <Option value="china">China</Option>
                        <Option value="usa">U.S.A</Option>
                        <Option value="vn">Viet Nam</Option>
                    </Select>
                </Form.Item>

                <Form.Item
                    name="colors"
                    label="Select your favourite color"
                    rules={[
                        {
                            required: true,
                            message: 'Please select your favourite colors!',
                            type: 'array',
                        },
                    ]}
                >
                    <Select mode="multiple" placeholder="Please select favourite colors">
                        <Option value="red">Red</Option>
                        <Option value="green">Green</Option>
                        <Option value="blue">Blue</Option>
                    </Select>
                </Form.Item>





                <Form.Item name="gender" label="Gender">
                    <Radio.Group>
                        <Radio value="male">Ma</Radio>
                        <Radio value="female">item 2</Radio>
                       
                    </Radio.Group>
                </Form.Item>

                <Form.Item name="radio-button" label="Radio.Button">
                    <Radio.Group>
                        <Radio.Button value="a">item 1</Radio.Button>
                        <Radio.Button value="b">item 2</Radio.Button>
                        <Radio.Button value="c">item 3</Radio.Button>
                    </Radio.Group>
                </Form.Item>

                <Form.Item name="checkbox-group" label="Checkbox.Group">
                    <Checkbox.Group>
                        <Row>
                            <Col span={8}>
                                <Checkbox
                                    value="A"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    A
              </Checkbox>
                            </Col>
                            <Col span={8}>
                                <Checkbox
                                    value="B"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                    disabled
                                >
                                    B
              </Checkbox>
                            </Col>
                            <Col span={8}>
                                <Checkbox
                                    value="C"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    C
              </Checkbox>
                            </Col>
                            <Col span={8}>
                                <Checkbox
                                    value="D"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    D
              </Checkbox>
                            </Col>
                            <Col span={8}>
                                <Checkbox
                                    value="E"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    E
              </Checkbox>
                            </Col>
                            <Col span={8}>
                                <Checkbox
                                    value="F"
                                    style={{
                                        lineHeight: '32px',
                                    }}
                                >
                                    F
              </Checkbox>
                            </Col>
                        </Row>
                    </Checkbox.Group>
                </Form.Item>

                <Form.Item name="rate" label="Rate">
                    <Rate />
                </Form.Item>



                <Form.Item
                    wrapperCol={{
                        span: 12,
                        offset: 6,
                    }}
                >
                    <Button type="primary" htmlType="submit">
                        Submit
        </Button>
                </Form.Item>
            </Form>



        )
    }
}

export default Home
